	function checkPosAdvices() {
		$('.advices__in.responsive').each(function() {
			var $items = $(this).find('.advices__in__item'),
				curRow = 0,
				curTop = $items.eq(0).offset().top;

			$items.each(function() {
				var newTop = $(this).offset().top;

				if (newTop > curTop) {
					curRow++;
					curTop = newTop;
				}

				if (curRow%2 === 0) {
					$(this).removeClass('right');
				} else {
					$(this).addClass('right');
				}
			});
		});
	}

